(function ($) {

    $('.meal-item__add-to-cart button').click(function (e) {
        e.preventDefault();

        var btn = $(this);
        var item = btn.val();
        var pattern = /^[0-9]+$/;
        var quantity = $(this).closest(".meal-item__add-to-cart").find('.meal-item-quantity .input-quantity').val();
        if( !pattern.test(quantity) ) {
            $(this).closest(".meal-item__add-to-cart").find('.error-message').addClass("show");
            $('.grid').isotope();
            setTimeout( () => {
                $(this).closest(".meal-item__add-to-cart").find('.error-message').removeClass("show");
                $('.grid').isotope();
            }, 3000);
        }

        var data = {
            'id': item,
            'quantity': quantity
        };

        btn.addClass('loading');

        $.post(meal_prep.mp_ajax_url.add_to_cart, data, function (response) {

            $.each( response.fragments, function( key, content ) {
                $(key).html(content);
            });

        }).done(function () {
            btn.removeClass('loading');
        })
    });

    if ($('.meal-item__mini-cart').length > 0) {
        var data = {
        };

        $.post(meal_prep.mp_ajax_url.get_refreshed_fragments, data, function (response) {
            $.each( response.fragments, function( key, content ) {
                $(key).html(content);
            });
        });
    }

    $(document).on('click', '.meal-item_clear-cart', function (e) {
        e.preventDefault();

        var data = {
        };

        $.post(meal_prep.mp_ajax_url.clear_cart , data, function (response) {

            $.each( response.fragments, function( key, content ) {
                $(key).html(content);
            });

        });
    })



})(jQuery);
(function ($) {

    if ($('#cart-content').length > 0){
        var data = {
        };

        $.post( meal_prep.mp_ajax_url.cart_content , data, function (response) {
            $('#cart-content').html(response.html);
        });
    }

})(jQuery);
(function ($) {

    if ($('#checkout-order-detail').length > 0) {
        var data = {};

        $.post( meal_prep.mp_ajax_url.checkout_order_detail , data, function (response) {
            $('#checkout-order-detail').html(response.html);

            $('.checkout-page [name=payment_method]').first().prop("checked", true).change();
        });
    }

    $(document).on('submit', '#checkout-content', function (e) {
        e.preventDefault();

        var form = $(this);

        var data = form.serialize();

        $.post(meal_prep.mp_ajax_url.checkout, data, function (response) {

            if (response.result == 'failure'){
                    $('#checkout-content .checkout-errors').remove();
                    $('#checkout-content').prepend('<div class="checkout-errors col-12">'+ response.messages +'</div>');

                    $('html, body').animate({
                        scrollTop: $('#checkout-content .checkout-errors').offset().top
                    }, 1000);

            }else{
                console.log(response);

                if (response.reload){
                    window.location.href = response.messages;
                }
            }
        });
    });

    // $(document).on('change', '#checkout-content input', function (e) {
    //     var form = $('#checkout-content');
    //     var data = form.serialize();
    //
    //     console.log(data);
    // });

    $(document).ready( () => {

        $('.billing_country')
            .dropdown({
                clearable: true,
                match: 'text',
                placeholder: 'Select country ...'
            })
        ;
    });

    $('.checkout-page').on('change', '[name=payment_method]', function (e) {
        var that = $(this);

        $('.checkout-page .payment-description').each(function () {
            $(this).hide();
        });

        that.closest('li').find('.payment-description').show();
    });

})(jQuery);